package com.operator.test;

public class ShiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// this operator is used to shift the bits of a number left to right 
		// similar t multiplying or dividing by two
		
		//left shift << 
		// multiply the number with some power of two
		
		// right shift >> 
		// dividing the number with some power of two
		
		int number1 = 10;
		System.out.println("Left Shift " + (number1 << 1));
		System.out.println("Right Shift " + (number1 >> 1));
		

	}

}
